#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;    vector<vector<int>> mat;    vector<int> ans;    // for(int i = 0; i< n;i++){    //     int e;     //     cin >> e;    //     ans.push_back(e);    // }     for(int i =0; i< n;i++){        for(int j = 0; j < n;j++){            int e;             cin >> e;            if(j == n - 1 || i == 0) ans.push_back(e);        }        // mat.push_back(cur);    }     // for(auto i:ans){    //     cout << i << " ";    // }    int total = 0, arr = 0;    for(int i = 0; i < (n*2) - 1; i++){        total += i+ 1;        arr += ans[i];    }    total += n*2;        cout << total - arr << " " ;    for(int i = 0 ; i < (n *2) - 1; i++){        cout << ans[i] << " ";    }    cout << endl; } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}