#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n,m,p,q;    cin >> n >> m >> p >> q;     if(n %p == 0 && (n/p) * q != m){        cout << "no\n";    }    else cout << "yes\n";} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}