#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n, x;    cin >> n >> x;     vector<int> v;     for(int i = 0;i < n; i++){        if(i != x) v.push_back(i);    }    if(x != n)v.push_back(x);     for(auto i : v) cout << i << " ";    cout << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}