#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    string s;    cin >> s;    int l = 0, r = 0;     int a = count(s.begin(), s.end(), '(');    int b = count(s.begin(), s.end(), ')');     for(auto i : s){        if(i == '(') l++;        else r++;         if(l == r) break;     }      if(r == b){        cout << "NO\n";    }    else cout << "YES\n";} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}