#include <bits/stdc++.h> using namespace std; void solve(){    int n;    cin >> n;    vector<int> arr, ans;    unordered_set<int> s;    for(int i = 0; i< n; i++) {        int a;        cin >> a;        if(s.find(a) != s.end()){            continue;        }        else{            s.insert(a);            arr.push_back(a);        }    }    if(s.size() == 1){        cout << "1\n";        return;    }     int c = 0;    int prev = arr[0];     prev = prev + 1;    ans.push_back(arr[0]);    int last = -1;     for(int i =0;i < arr.size(); i++){        if((-last + arr[i]) > 1){            last = arr[i];            c++;        }        // else{        //     ans.push_back(arr[i]);        //     c++;        // }     }     // for(auto i : ans) cout <<  i << " ";    // cout << endl;          cout << c << endl;       } int main(){    int t;    cin >> t;    while(t--) solve();}