#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n, s;    cin >> n >> s;    vector<int> v(n);    int one = 0, two = 0, zero = 0;    for(int i =0; i< n; i++){        cin >> v[i];        if(v[i] == 1) one++;        else if(v[i] == 2) two++;        else zero++;     }    int count = one + two * 2;     if(count == s || one +two * 2 + 2 <= s){        cout << "-1\n";    }    else{                for(int i = 0; i < zero; i++) cout << "0 ";        for(int i = 0; i < two ; i++) cout << "2 ";        for(int i = 0;i < one; i++) cout << "1 ";        cout << endl;            }    } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}