#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC     int n, k;    cin >> n >> k;     multiset<int> a,b;     for(int i = 0; i < n ; i++){        int l;        cin >> l ;        a.insert(l % k);    }    for(int i = 0; i < n ; i++){        int l;        cin >> l ;        b.insert(l % k);    }          for (auto i : a) {    auto it = b.find(i);     if (it != b.end()) {        b.erase(it);    } else {        int alt = abs(i - k);        auto it2 = b.find(alt);         if (it2 != b.end()) {            b.erase(it2);        } else {            cout << "NO\n";            return;        }    }}       cout << "YES\n";      } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}