#include <bits/stdc++.h>using namespace std; vector<long long> v; void solve() {    long long n;    cin >> n;     long long curr = 0; // initialize properly    int p = 0;    while(n){        curr += (n % 3) * v[p];        n /= 3;        p++;    }    cout << curr << endl;    } int main() {    ios::sync_with_stdio(false);    cin.tie(nullptr);     long long c = 3, x = 1;    for (int i = 0; i < 21; i++) {        v.push_back(c);        c = 3 * c + x;        x *= 3;    }     int t;    cin >> t;    while (t--) solve();}