#include <bits/stdc++.h>using namespace std; #define ll long long void solve(){    // LOGIC    ll n, k;    cin >> n >> k;        vector<ll> a(n);    for(int i = 0; i<n;i++){        cin >> a[i];    }     ll d = k + 1;    for(int i =0; i< n; i++){        ll cur = a[i] % d;        cout << a[i] + (cur * k ) << " ";    }    cout << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}