#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;        vector<int> v(n);     for(int i = 0;i < n; i++) cin >> v[i];     int l = 0, r  = n - 1;     for(int i = 0; i < n; i++){        for(int j = 0; j < n; j++){            v[j] -= 1;        }        while(l< r){            if(v[l] == 0){                l++;                break;            }            else if(v[r] == 0){                r--;                break;            }            else{                cout << "NO\n";                return;            }        }     }    cout << "YES\n";  } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}