#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;        vector<int> v(n), pref(n);     for(int i = 0; i < n; i++){        cin >> v[i];    }    pref[0]= v[0];     for(int i = 1; i < n; i++){        pref[i] = pref[i-1] + v[i];    }    int a , b , c;    for(int i = 0; i < n - 2; i++){        for(int j = i + 1; j < n - 1; j++){            a = pref[i] % 3;            b = (pref[j] - pref[i]) % 3;            c = (pref[n -1 ] - pref[j]) % 3;            if(a == b && a == c){                cout << i+1 << " " << j + 1 << endl;                return;            }             if(a != b && b != c && a != c){                cout << i+1 << " " << j + 1 << endl;                return;            }        }    }    cout << "0 0\n";} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}