#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;        vector<int> v(n), ans;     for(int i = 0; i< n; i++) cin >> v[i];     unordered_map<int, int> m;     for(auto i : v) m[i]++;     int c = 0;    for(auto i:m) {ans.push_back(i.second); c++;}     sort(ans.begin(), ans.end());     long long sum = INT_MIN;     for(int i = 0; i< ans.size(); i++){        sum = max(sum, ans[i] * c * 1LL);        c--;    }    cout << sum << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}