#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    long long a, b;    cin >> a >> b;    if(a < b){        cout << "2\n";        return;    }    else if(a > b){        if(a - b == 1 || b == 1){            cout << "-1\n";            return;        }        else{            cout<< 3 << endl;            return;        }    }    else    cout << -1 << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}