#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;     for(int i = n; i >= 1; i--){        cout << i << " ";    }    cout << n << " ";     for(int i = 1; i< n; i++){        cout << i << " ";    }    cout << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}