#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n , m , x, y;    int c = 0;    cin >> n >> m >> x >> y;    vector<int> vn(n), vm(m);     for(int i = 0; i < n; i++){        cin >> vn[i];        if(vn[i] <= y ) c++;     }    for(int i = 0; i < m; i++){        cin >> vm[i];        if(vm[i] <= x ) c++;     }    cout << c << endl;  } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}