#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n, m;    cin >> n >> m;    int pa, pb , a , b, dif;    // cin >> pa >> pb;    pa = 0;    pb = 0;    int p = 0;    for(int i = 0; i< n; i++){        cin >> a >> b;        p += a- pa;        if((a - pa + 10) %2 != (b - pb + 10)%2) p--;        pa = a;        pb = b;     }    p+= (m - pa);    cout << p << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}