#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;     vector<long long> odd, even;     for(int i = 0; i < n; i++){        long long a;        cin >> a;        if(a&1){            odd.push_back(a);        }        else{            even.push_back(a);        }    }     if(odd.size() == 0){        cout << 0 << endl;        return;    }     sort(odd.rbegin(), odd.rend());     long long sum = accumulate(even.begin(), even.end(), 0ll);     int s = odd.size();    if(s == 1){        cout<< sum + odd[0] << endl;        return;    }     for(int i =0; i< (s+1)/2; i++){        sum += odd[i];    }      cout << sum << endl;  } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}