#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;     vector<int> v(n);    long long sum = 0, odd = 0;     for(int i = 0; i< n; i++){        int a ;        cin >> a;        if(a == 0) sum++;        else if(a == -1) {            odd++;        }    }    if(odd & 1) sum += 2;    cout << sum << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}