#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;     vector<int> v(n);     for(int i = 0; i < n; i++){        cin >> v[i];    }     sort(v.begin(), v.end());     long long m = INT_MIN;     for(int i = 0; i< n - 1; i+= 2){        m = max(m, abs(v[i] - v[i+1]) * 1LL);    }    cout << m << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}