#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n, k,  c = 0;    cin >> n >>k;     vector<int> v(n);    unordered_set<int> s;     for(int i = 0; i< n; i++) cin >> v[i];     sort(v.begin(), v.end());     for(int i = 0; i < n; i++){        if(v[i] < k) s.insert(v[i]);        else if(v[i] == k) c++;    }     int count = s.size();    count = k - count;     cout << max(count , c) << endl;    // cout << count << " "  << c << endl; } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}