#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n, c = 0;    cin >>n;    string s;     cin >> s;     if(n <=2){        cout << "0\n";        return;    }     // for(int i = 1; i < n - 1;i++){    //     if(s[i] != s[i - 1] && s[i] != s[i+1]) c++;    // }     // cout << ceil(c/2) <<  endl;     vector<long long> a, b;     for(int i = 0; i < n; i++){        if(s[i] == 'a') a.push_back(i);        else b.push_back(i);    }     if(a.size() <= 1 || b.size() == 0){        cout << "0\n";        return;    }    if(b.size() <= 1 || a.size() == 0){        cout << "0\n";        return;    }     vector<long long> sa, sb;     for(int i = 0; i< a.size(); i++) sa.push_back(a[i] - i);    for(int i = 0; i< b.size(); i++) sb.push_back(b[i] - i);     long long midA = sa[a.size() /2], midB = sb[b.size()/2];     long long costa = 0, costb = 0;     for(int i = 0; i< a.size(); i++) costa += llabs(sa[i] - midA);    for(int i = 0; i< b.size(); i++) costb += llabs(sb[i] - midB);     cout << min(costa, costb) << endl; } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int t;    cin >> t;    while(t--) solve();}