#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n , k;    cin >> n >> k;    vector<int> v(k);     for(int i = 0; i< k ; i++) cin >> v[i];     if(v[k - 1] - v[0] == k - 1){        cout << n - v[k- 1] + 1 << endl;    }    else {        cout << "1\n";    }} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}