#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;    set<int> s;     vector<int> v(n);     for(int i = 0; i < n; i++) {        int a;        cin >> a;        s.insert(a);    }    if(n == 1) {        cout << "1\n";        return;    }    int c = s.size();     cout << 2 * c - 1 << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}