#include <bits/stdc++.h>using namespace std;  long long thief(long long r1,long long c1,long long r2,long long c2){    return llabs(r1-r2) + llabs(c1-c2);}long long police(long long r1,long long c1,long long r2,long long c2){    return max(llabs(r1-r2), llabs(c1-c2));}    void solve(){    // LOGIC    long long n, rt, ct, rp, cp;    cin >> n >> rt >> ct >> rp >> cp;       long long ans = 0;    long long dr = abs(rt - rp);    if(dr > 0){        long long s;        if(rt < rp) s= rt;        else  s= n - rt;        ans = max(ans, dr+s);    }         long long dc = abs(ct - cp);    if(dc > 0){        long long s;        if(ct <cp) s = ct;        else s = n - ct;        ans = max(ans, dc + s);    }     cout << ans << endl;     } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}