#include <bits/stdc++.h>using namespace std; #define ll long long void solve(){    // LOGIC    ll x,y,z;    cin >> x >> y >> z;      // ll a = x|z, b = x|y, c = y|z;     // cout << a << " " << b << " " << c;     if(((x&y) & ~z )|| ((y & z) & ~x) || (z & x) & ~y) cout << "NO";    else cout << "Yes";    cout << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}