#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n, k;    cin >> n >> k;    int t = k;     if(abs(k - (n* n)) == 1){ cout << "no\n"; return;}    else cout << "yes\n";     for(int i = 0; i < n; i++){        for(int j = 0; j < n; j++){        if(t > 0) {cout << "U";        t--;}        else{            if(i != n - 1){                cout << "D";            }            else{                if(j != n - 1){                    cout << "R";                }                else{                    cout << "L";                }            }        }    }    cout << endl;    } } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}