#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;    vector<int> v( 2 * n);     for(auto &i : v) cin >> i;      map<int, int> m;     for(auto i : v) m[i]++;     if(m.size() == 1){        if(n & 1){            cout << 2 << endl;        }        else cout << 0 << endl;         return;    }      int c = m.size(), o = 0, e = 0;     for(auto i : m){        if(i.second &1){            o++;        }        else e++;    }      if((c % 2) != (n % 2)) c--;    c = max(c, 0);      cout << o + 2 * min(c, e) << endl;    } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}