#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;    set<int> v;    bool z = false;     for(int i = 0; i < n; i++) {int a; cin >> a; v.insert(a);}     int f = 0;    for(auto i : v){        // cout << i << " " ;        if(f == i){            f++;        }        else {            cout << f << endl;            return;        }    }    cout << f << endl;      } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}