#include <bits/stdc++.h>using namespace std; #define ll long long void solve(){    // LOGIC    ll n;    cin >> n;     vector<ll> v(n);     for(int i = 0; i< n ;i++) cin >> v[i];     ll f = 2, dis = 1;     vector<ll> ans = {1};     for(int i = 1; i < n; i++){        ll dif = v[i] - v[i - 1];        if(dif == i + 1){            ans.push_back(f);            f+= 1;        }        else {            ans.push_back(ans[i - dif]);        }    }     for(auto i  : ans) cout<< i << " ";    cout<< endl; } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}