#include <bits/stdc++.h>using namespace std; #define ll long long void solve(){    // LOGIC    ll a,b;    cin >> a >> b;     if(a == b) {        cout << 0 << endl;        return;    }            ll msba = 31 - __builtin_clz(a);    ll msbb = 31 - __builtin_clz(b);     if(msbb > msba) {        cout << -1 << endl;        return;    }     ll p = 1 << msba;    if(msba == msbb){        ll la = a - p;        ll lb = b - p;        ll x = la ^ lb;        cout << 1 << endl << x << endl;    }    else {        ll la = a & (p - 1);        ll x  = la ^ b;        if(x != 0){            cout << 2 << endl << x << " " << p << endl;        }        else cout << 1 << endl << p << endl;    } }  int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}