#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;    vector<int> v(n);     for(auto &i : v) cin >> i;     map<int, int>m;    for(auto i : v) m[i]++;     sort(v.begin(), v.end());     for(int i = 0;i < n - 1 ;i++){        if((i + 1) & 1){            continue;        }        else{            if(v[i] != v[i + 1]){                cout << "NO\n";                return;            }        }    }     cout << "YES\n";     // if()} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}