#include <bits/stdc++.h>using namespace std; #define ll long long void solve(){    // LOGIC    ll n;    cin >> n;    vector<ll> v(n);     for(auto &i : v) cin >> i;     ll x;    cin >> x;     ll s = *min_element(v.begin(), v.end());    ll b = *max_element(v.begin(), v.end());     if( s<= x && x <= b) cout << "YES\n";    else cout << "NO\n";} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}