#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    string a,b;    int n;    cin >> n >> a >> b;     vector< int> x(26, 0), y(26, 0);     for(auto i:a) x[i - 'a']+= 1;    for(auto i:b) y[i - 'a']+= 1;     for(int i = 0; i < 26; i++) {        if(x[i] != y[i]) {            cout << "NO\n";            return;        }        // cout << x[i] << "a b" << y[i] << " ";    }     // for()     cout << "YES" << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}