#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;    vector<int> v(n);    bool odd = false, even = false;     for(int i = 0; i < n; i++){        cin >> v[i];        if(v[i] & 1) odd = true;        else even = true;    }     if(odd == true && even == true){        sort(v.begin(), v.end());    }     for(auto i : v){        cout << i << " ";    }    cout << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}