#include <bits/stdc++.h>using namespace std; vector<bool> vis;vector<pair<int, int>>  v;int n; void dfs(int x){    vis[x] = true;     for(int i = 0; i< n ; i++){        if(!vis[i] && (v[i].first == v[x].first || v[i].second == v[x].second)){            dfs(i);        }    }} void solve(){    // LOGIC    // int n;    cin >> n;     int c = 0;    vis.resize(n, false);      unordered_map<int, int> l,r;     for(int i = 0; i < n; i++){        int cx, cy;        cin >> cx >> cy;        v.push_back({cx, cy});            }     for(int i = 0; i< n ; i++){        if(!vis[i]){            dfs(i);            c++;        }    }       cout << c - 1<< endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     // int n;    // cin >> n;    // while(n--)     solve();}