#include <bits/stdc++.h>using namespace std; #define ll long long void solve(){    // LOGIC    int n, s;    cin >> s >> n;     vector<pair<ll, ll>> v;     for(int i = 0;i < n; i++){        int a, b;        cin >> a >> b;        v.push_back({a,b});    }     int c = s;     sort(v.begin(), v.end());     for(int i = 0;i < n ; i++){        if( c > v[i].first){            c += v[i].second;        }        else{            cout << "NO\n";            return;        }    }     cout << "YES\n";  } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from coutsolve();}