#include <bits/stdc++.h>using namespace std; bool isPrime(long long a){    if(a < 2) return false;     for(int i = 2; i * i<= a + 1; i++){        if(a%i == 0) return false;    }    return true;} void solve(){    // LOGIC    long long a;    cin >> a;    long long root = sqrt(a);    if((a == root * root )&& isPrime(root)){        cout << "YES\n";    }    else{        cout << "NO\n";    }} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}