#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;    vector<int> v(n);    int even = -1, odd = - 1, ec = 0, oc = 0;    for(int i = 0; i<n; i++){        cin >> v[i];        if(v[i] & 1){            odd = i;            oc++;        }        else{            even = i;            ec++;        }    }    if(ec== 1) cout<< even + 1;    else cout << odd + 1;    cout << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout   solve();}