#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;     string s;    cin >> s;    int c = 0;     for(int i = 0; i< n - 1; i++){        if(s[i] == s[i + 1]) c++;    }      cout << c << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout      solve();}