#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n, k;    cin >> n >> k;    vector<int> v(n);     for(int i = 0; i< n ; i++) cin >> v[i];     int i = 0, j = 0;    int s = 0, c = 0;    s = 0;    c = 0;    int m = 0;    while( j < n){        s += v[j];        c++;        while(s > k && i <= j){            s -= v[i];            i++;            c--;        }        j++;        m = max(m, c);    }     cout << m << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     solve();}