#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n, x = 0, y = 0, z = 0;    cin >> n;     vector<int> v(n), a,b,c;     for(int i = 0;i < n; i++){        cin >> v[i];        if(v[i] < 0) x++;        else if(v[i] > 0) y++;        else z++;    }     if(y> 0){        for(auto i : v){            if(i < 0 && (a.size() == 0)) a.push_back(i);            else if(i > 0) b.push_back(i);            else c.push_back(i);        }    }    else{        for(auto i: v){            if(i < 0 && (a.size() == 0)) a.push_back(i);            else if(i < 0 && b.size() <2) b.push_back(i);            else c.push_back(i);         }    }     cout << a.size() << " ";    for(auto i : a){        cout << i << " ";    }    cout << endl;    cout << b.size() << " ";    for(auto i : b){        cout << i << " ";    }    cout << endl;    cout << c.size() << " ";    for(auto i : c){        cout << i << " ";    }    cout << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout  solve();}