#include <bits/stdc++.h>using namespace std; // #define int long long void solve(){    // LOGIC    string s;    cin >> s;    int n;    cin >> n;    vector<int> v(s.size() + 1, 0);     for(int i = 1;i < s.size(); i++){        if(s[i] == s[i - 1]){            v[i] += v[i - 1] + 1;        }        else v[i ] = v[i - 1];    }     // for(auto i : v) cout << i << " ";    // cout << endl;     while(n--){        int x, y;        cin >> x >> y;        cout << v[y - 1 ] - v[ x - 1 ] << endl;     } } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout  solve();}