#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;    vector<int> v(n);     int o = 0;    for(int i = 0; i< n; i++){        cin >> v[i];        if(v[i] == 1) {v[i] = -1; o++;}        else v[i] = 1;    }        if(n == 1) {        cout << (v[0] == -1 ? 0 : 1) << "\n";        return;    }    int len = 0, totalMax = INT_MIN, cur = 0;     for(int i = 0; i< n;i++){        cur = max(v[i], v[i]+ cur);        totalMax = max(cur,totalMax);     }    cout << o + totalMax<< endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout   solve();}