#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n, m;    cin >> n >>m;     set<int> s;     for(int i = 0; i < m; i++){        int u, v;        cin >> u >> v;        s.insert(u);        s.insert(v);    }     int c = 1;    for(auto i: s){        if(c != i) break;        c++;    }     cout << n -1 << endl;    for(int i = 1; i < n + 1; i++){        if(i == c) continue;        cout << i << " " << c;        cout << endl;    } } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout  solve();}