#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n,k;    cin >>n >>k;    vector<int> v(k);     for(int i = 0; i < k; i++) cin >> v[i];        sort(v.begin(), v.end());     int m = INT_MAX;    for(int i = 0; i<= k - n; i++){        m = min(m, v[i + n - 1] - v[i]);    }    cout <<m<< endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     solve();}