#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    long long n , m;    cin >>n >>m;     long long count = 0, p = 1;     for(int i = 0; i < m; i++){        long long cur;        cin >> cur;        // if(p == cur) continue;        if(p <= cur){            count += cur - p;            p = cur;        }        if(p > cur){            count += (n - p) + cur;            p = cur;        }     }    cout << count << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     solve();}