#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;    unordered_map<int, int> s;     vector<int> v(n);     for(int i = 0; i < n; i++) cin >> v[i];     int c = 0;    for(int i = 0; i < n; i++){        if(i == v[i]) c++;        else {            s[v[i]] = i;        }    }     // cout << c << endl;      for(int i = 0; i< n; i++){        if(i != v[i]){            if(s[i] == v[i]){                cout << c +2 << endl;                return;            }        }    }     if( c + 1 <= n)    cout << c + 1 << endl;    else cout << c << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;  solve();}