#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n, k;    cin >> n >>k;    vector<int> v(n);    for(int i = 0;i  < n;  i++) cin>> v[i];     vector<int> pref(n + 1 );    pref[0] = 0;    pref[1] = v[0];    for(int i = 2; i<= n; i++){        pref[i] = v[i - 1] + pref[i - 1];    }        int m = INT_MAX;    int ans = -1;    for(int i = 0; i <= n - k ; i++){        if(m > pref[i+k ] - pref[i]){            m = pref[i+k ] - pref[i];            ans = i + 1;        }    }     cout << ans << endl;   } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout solve();}