#include <bits/stdc++.h>using namespace std; void solve() {    int n, q;    cin >> n >> q;    vector<int> v(n);    for (int i = 0; i < n; i++) {        cin >> v[i];    }     unordered_set<int> s;    int c = 0;    vector<int> pref(n);     for (int i = n - 1; i >= 0; i--) {        if (s.find(v[i]) != s.end()) {            pref[i] = c;        } else {            s.insert(v[i]);            c++;            pref[i] = c;        }    }     for (int i = 0; i < q; i++) {        int k;        cin >> k;        cout << pref[k - 1] << "\n";  // fixed indexing    }} int main() {    ios::sync_with_stdio(false);    cin.tie(nullptr);    solve();}