#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n,d;    cin >> n >> d;    vector<pair<int, int>>v;    for(int i = 0; i < n; i++){        int a, b;        cin >> a >> b;        v.push_back({a,b});    }    int s = 0 , b = 0;    for(int i = 0; i < n; i++){        s+= v[i].first;        b += v[i].second;    }     // for(int i = 0 ; i< n; i++)    if( s <= d && d <= b){        cout << "YES\n";        vector<int> ans(n, 0);        for(int i = 0; i < n; i++){            ans[i] = v[i].first;            d-= v[i].first;         }        int i = 0;        while (d > 0 && i < n) {            int can_add = v[i].second - ans[i];            int add = min(can_add, d);            ans[i] += add;            d -= add;            i++;        }         for(int i = 0; i < n;i++){            cout << ans[i] << " ";        }        cout << endl;     }    else{        cout << "NO\n";    }}